;; **
;;; And if a unit fails, we can still check the results.
;; **

;; @@
(set-unit-failure-policy false)

(-> (newgrape) (cheese-pizza 1000) (cheese-pizza 1000) view)

(println "pre-condition" (pre?))
(println "post-condition" (post?))
