;; **

;; @@
(rule 'related-to ['name]
      :read (pattern (node 'p :label "Person" :asserts {:name "'&name'"})
                     (node 'a)
                     (path ">:works_for*..3 >:parent_of*" :src 'p :tar 'a)))
