;; =>
;;; {"type":"html","content":"<img src=\"data:image/png;base64,\" width=\"160\" height=\"186\" alt=\"\" />","value":"#object[java.awt.image.BufferedImage 0x1843f805 \"BufferedImage@1843f805: type = 6 ColorModel: #pixelBits = 32 numComponents = 4 color space = java.awt.color.ICC_ColorSpace@3ebd1ea7 transparency = 3 has alpha = true isAlphaPre = false ByteInterleavedRaster: width = 160 height = 186 #numDataElements 4 dataOff[0] = 3\"]"}
;; <=

;; @@
(rule 'JohnLikesKim!
      :read
       (pattern
        (node 'n1 :label "Kim")
        (node 'n2 :label "John"))
       :create
       (pattern
        (edge :label "likes" :src 'n2 :tar 'n1)))
;; @@
;; =>
;;; {"type":"html","content":"<img src=\"data:image/png;base64,\" width=\"160\" height=\"186\" alt=\"\" />","value":"#object[java.awt.image.BufferedImage 0x21ee98ed \"BufferedImage@21ee98ed: type = 6 ColorModel: #pixelBits = 32 numComponents = 4 color space = java.awt.color.ICC_ColorSpace@3ebd1ea7 transparency = 3 has alpha = true isAlphaPre = false ByteInterleavedRaster: width = 160 height = 186 #numDataElements 4 dataOff[0] = 3\"]"}
;; <=

