;; @@
(rule 'setup3!
      :create
       (pattern
        (node 'n1 :label "Kim")
        (node 'n2 :label "John")
        (edge :label "likes" :src 'n1 :tar 'n2)))
;; @@
;; =>
;;; {"type":"html","content":"<img src=\"data:image/png;base64,\" width=\"115\" height=\"186\" alt=\"\" />","value":"#object[java.awt.image.BufferedImage 0x3fe6829a \"BufferedImage@3fe6829a: type = 6 ColorModel: #pixelBits = 32 numComponents = 4 color space = java.awt.color.ICC_ColorSpace@3ebd1ea7 transparency = 3 has alpha = true isAlphaPre = false ByteInterleavedRaster: width = 115 height = 186 #numDataElements 4 dataOff[0] = 3\"]"}
